// app/api/crypto-sentiment/route.ts
import { NextResponse } from 'next/server';
import { spawn } from 'child_process';
import { spawnSync } from 'child_process';
import path from 'path';

// Correct path to Python script - adjust based on your actual structure
const PYTHON_SCRIPT = path.join(process.cwd(), 'scripts', 'crypto_sentiment_analysis.py');
const TIMEOUT_MS = 30000; // 30 seconds

// Fallback data
const FALLBACK_DATA = [
  {
    coin: "Bitcoin",
    price: 43210.5,
    change_24h: 2.3,
    sentiment: "Bullish",
    confidence: 0.78,
    timestamp: new Date().toISOString()
  },
  {
    coin: "Ethereum",
    price: 2270.1,
    change_24h: -1.2,
    sentiment: "Neutral",
    confidence: 0.55,
    timestamp: new Date().toISOString()
  },
  {
    coin: "Solana",
    price: 98.4,
    change_24h: 5.7,
    sentiment: "Bullish",
    confidence: 0.82,
    timestamp: new Date().toISOString()
  }
];

export async function GET(request: Request) {
  const { searchParams } = new URL(request.url);
  const useFallback = searchParams.get('fallback');
  
  if (useFallback === 'true') {
    return NextResponse.json(FALLBACK_DATA);
  }

  try {
    console.log('API called — attempting Python execution');  // Logs to Vercel
    const result = await executePythonScript();
    console.log('Python succeeded — returning live data');
    return NextResponse.json(result);
  } catch (error) {
    console.error('Full API error:', error);
    return NextResponse.json(FALLBACK_DATA);
  }
}

async function executePythonScript(): Promise<any[]> {
  return new Promise((resolve) => {
    console.log('Script path:', PYTHON_SCRIPT);  // Check if file exists
    console.log('Environment:', { VERCEL: !!process.env.VERCEL, cwd: process.cwd() });

    // Robust Python command
    const pythonCmds = process.env.VERCEL 
      ? ['/usr/bin/python3', 'python3', 'python']  // Vercel paths first
      : ['python', 'python3'];
    
    let pythonCmd = null;
    for (const cmd of pythonCmds) {
      try {
        const test = spawnSync(cmd, ['-c', 'print("Python OK")']);
        if (test.status === 0) {
          pythonCmd = cmd;
          break;
        }
      } catch {}
    }
    
    if (!pythonCmd) {
      console.error('No Python found — fallback');
      return resolve(FALLBACK_DATA);
    }
    
    console.log('Using Python:', pythonCmd);

    const python = spawn(pythonCmd, [PYTHON_SCRIPT], {
      cwd: process.cwd(),
      timeout: TIMEOUT_MS,
    });

    let data = '';
    let errorOutput = '';
    let resolved = false;

    const timeout = setTimeout(() => {
      if (!resolved) {
        resolved = true;
        python.kill('SIGTERM');
        console.warn('Timeout — fallback');
        resolve(FALLBACK_DATA);
      }
    }, TIMEOUT_MS);

    const resolveSafely = (result: any[]) => {
      if (!resolved) {
        resolved = true;
        clearTimeout(timeout);
        resolve(result);
      }
    };

    python.stdout.on('data', (chunk) => {
      data += chunk.toString();
    });

    python.stderr.on('data', (chunk) => {
      errorOutput += chunk.toString();
    });

    python.on('close', (code) => {
      if (resolved) return;

      console.log('Python closed:', { code, dataLength: data.length, errorLength: errorOutput.length });
      console.log('Raw stdout preview:', data.substring(0, 500));  // First 500 chars
      console.log('Raw stderr preview:', errorOutput.substring(0, 500));

      if (code === 0 && data.trim()) {
        try {
          const parsedData = JSON.parse(data.trim());
          if (Array.isArray(parsedData) && parsedData.length > 0) {
            console.log('Success — live data loaded');
            resolveSafely(parsedData);
            return;
          }
        } catch (parseError) {
          console.error('Parse error:', parseError, 'Raw data:', data.substring(0, 1000));
        }
      }
      
      console.error('Failed — fallback (code:', code, 'stderr:', errorOutput);
      resolveSafely(FALLBACK_DATA);
    });

    python.on('error', (error) => {
      console.error('Spawn error:', error.message);
      resolveSafely(FALLBACK_DATA);
    });
  });
}

export const dynamic = 'force-dynamic';
export const maxDuration = 60;